SELECT m.id, m.nameRussian, m.nameNative, m.yearOfRelease, m.description,
                                       m.rating, m.price, m.picturePath
        FROM movie m;

SELECT mc.movieId, c.id, c.country
       FROM country c, movie2country mc
       WHERE c.id = mc.countryId;

SELECT mg.movieId, g.id, g.genre
       FROM genre g, movie2genre mg
       WHERE g.id = mg.genreId;
